mapper.select:
select qno, userid, subject, content, regtime
from question
where qno = #{qno}

mapper.update:
update question
set subject = #{subject}, content = #{content}, regtime = now() 
where qno = #{qno}

mapper.select:
select subject, content, regtime
from answer
where qno = #{qno}

mapper.update:
update answer
set subject = #{subject}, content = #{content}, regtime = now() 
where qno = #{qno}
